import { sudokuParser } from './SudokuParser';

export {sudokuParser};

export function parseSudokuCode(inputCode) {
  return sudokuParser.parse(inputCode);
}

export function validateSudokuCode(inputCode) {
  return sudokuParser.validate(inputCode);
}

export function encodeSudoku(sudokuGrid) {
  return sudokuParser.encode(sudokuGrid);
}