/**
 * 解析策略抽象接口（策略链核心）
 * 每个策略必须实现：
 * 1. validate：验证输入是否归当前策略解析
 * 2. parse：验证通过则解析，否则返回null
 */
export class ParseStrategy {
  /**
   * 验证输入是否归当前策略解析
   * @param {string} input - 输入内容（Sencode/URL/其他）
   * @returns {boolean} true=归当前策略解析，false=不归
   */
  validate(input) {
    throw new Error('子类必须实现validate方法');
  }

  /**
   * 解析输入为9x9数独网格
   * @param {string} input - 输入内容
   * @returns {number[][]|null} 解析成功返回网格，失败返回null
   * @throws {Error} 验证通过但解析失败时抛出异常（非“不归我解析”的情况）
   */
  parse(input) {
    throw new Error('子类必须实现parse方法');
  }
}